const App = () => (<Counter></Counter>)

class Counter extends Component {
  constructor(props){
    super(props);
    this.state = {count: 0}
  }
  handlePlusButton = () => {
    this.setState()
  }
  render() {
    return (
      <React.Fragment>
        <div>count: {this.state.count}</div>
        <button onClick={this.handlePlusButton}>+1</button>
        <button>-1</button>
      </React.Fragment>
    );
  }
}
